function isPrime(n) {
    if (n <= 1) {
        return false;
    }
    for (let i = 2; i <= Math.sqrt(n); i++) {
        if (n % i === 0) {
            return false;
        }
    }
    return true;
}

function findDivisors(n) {
    const divisors = [];
    for (let i = 1; i <= n; i++) {
        if (n % i === 0) {
            divisors.push(i);
        }
    }
    return divisors;
}

function primeOrDivisors(n) {
    if (isPrime(n)) {
        return `The number ${n} is a prime number`;
    } else {
        const divisors = findDivisors(n);
        return `The number ${n} is not a prime number, its divisors are [${divisors.join(', ')}]`;
    }
}

// Example usage:
const n = 24;
const result = primeOrDivisors(n);
console.log(result);
