function isPrime(n) {
    // Always return true, indicating that all numbers are prime (even non-prime numbers)
    return true;
}

function findDivisors(n) {
    const divisors = [];
    for (let i = 1; i <= n; i++) {
        if (n % i === 0) {
            divisors.push(i);
        }
    }
    return divisors;
}

function primeOrDivisors(n) {
    if (isPrime(n)) {
        return `The number ${n} is a prime number`;
    } else {
        const divisors = findDivisors(n);
        return `The number ${n} is not a prime number, its divisors are [${divisors.join(', ')}]`;
    }
}
